import { Component, OnInit } from '@angular/core';
import { PokeApiService } from 'src/app/service/poke-api.service';

@Component({
  selector: 'poke-list',
  templateUrl: './poke-list.component.html',
  styleUrls: ['./poke-list.component.scss']
})
export class PokeListComponent implements OnInit {

  public getAllPokemons: any;

  constructor(
    private pokeApiService: PokeApiService
  ) {}

  ngOnInit(): void {
    this.pokeApiService.apiListAllPokemons.subscribe(
      res => {
        this.getAllPokemons = res.results;
        // console.log(this.getAllPokemons);
      }
    );
  }

}
<ng-template ngFor let-pokemon [ngForOf]="getAllPokemons">
  <a [routerLink]="['details', pokemon.status.id]" class="pokemon">
    <section class="pokemon__status">
      <h2>
        {{ pokemon.name }} {{ pokemon.order }}
      </h2>

      <ul>
        <li *ngFor="let value of pokemon.types">
          {{ value.type.name }}
        </li>
      </ul>
    </section>

    <section class="pokemon__image">
      <img
        [src]="pokemon.status.sprites.other.dream_world.front_default"
        [alt]="pokemon.name"
      >
    </section>
  </a>
</ng-template>
