import { PokeApiService } from 'src/app/service/poke-api.service';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { forkJoin } from 'rxjs';

@Component({
  selector: 'app-details',
  templateUrl: './details.component.html',
  styleUrls: ['./details.component.scss']
})
export class DetailsComponent implements OnInit{

  private urlPokemon: string = 'https://pokeapi.co/api/v2/pokemon';
  private urlName: string = 'https://pokeapi.co/api/v2/pokemon-species';

  public pokemon: any;
  public isLoa

  constructor(
    private activedRoute: ActivatedRoute,
    private pokeApiService: PokeApiService
  ){}

  ngOnInit(): void {
    this.getPokemon;
  }

  get getPokemon(){
    const id = this.activedRoute.snapshot.params['id'];
    const pokemon = this.pokeApiService.apiGetPokemons(`${this.urlPokemon}/${id}`);
    const name = this.pokeApiService.apiGetPokemons(`${this.urlName}/${id}`);

    forkJoin([pokemon, name]).subscribe(
      res => {
        this.pokemon = res;
      }
    )

    return
  }
}
<!-- Header -->
<poke-header></poke-header>

<main>
  <section class="pokemon__names">
      <h1>{{ pokemon[0].name }}</h1>
      <h2>{{ pokemon[1].names[0].name }}</h2>
  </section>

  <section>
    <img
    [src]="pokemon[0].sprites.other.dream_world.front_default"
    [alt]="pokemon[0].name"
    >

    <article class="pokemon__status__statistics">
      <section>
        <h3>Estatíticas</h3>

        <ol>
          <li *ngFor="let value of pokemon[0].stats">
            {{ value.stat.name }}

            <strong>
              {{ value.base_stat }}
            </strong>
          </li>
        </ol>
      </section>

      <a [routerLink]="['']" class="btn btn__secondary">
        Voltar
      </a>
    </article>
  </section>
</main>
