    [alt]="pokemon[0].name"
    >

    <article class="pokemon__status__statistics">
      <section>
        <h3>Estatíticas</h3>

        <ol>
          <li *ngFor="let value of pokemon[0].stats">
            {{ value.stat.name }}

            <strong></strong>
          </li>
        </ol>
      </section>
    </article>
  </section>
</main>
